import React, { useEffect, useState, useRef, useCallback } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import "./Home.css";

// ===================== Add task 22 imports here =====================

const LIMIT = 10;

const Home = () => {
  const [doctors, setDoctors] = useState([]);
  const [searchQuery, setSearchQuery] = useState("");
  const [page, setPage] = useState(1);
  const [hasMore, setHasMore] = useState(true);
  const observer = useRef();
  const navigate = useNavigate();

  const fetchDoctors = async (pageNum, search = "") => {
    try {
      const res = await axios.get("http://localhost:3000/doctors", {
        params: {
          page: pageNum,
          limit: LIMIT,
          search: search.trim()
        },
      });

      const newDocs = res.data;

      if (pageNum === 1) {
        setDoctors(newDocs);
      } else {
        setDoctors((prev) => [...prev, ...newDocs]);
      }

      if (newDocs.length < LIMIT) setHasMore(false);
    } catch (err) {
      console.error("Failed to fetch doctors:", err);
    }
  };

  useEffect(() => {
    fetchDoctors(page, searchQuery);
  }, [page, searchQuery]);

  const lastDoctorRef = useCallback(
    (node) => {
      if (observer.current) observer.current.disconnect();
      observer.current = new IntersectionObserver((entries) => {
        if (entries[0].isIntersecting && hasMore) {
          setPage((prev) => prev + 1);
        }
      });
      if (node) observer.current.observe(node);
    },
    [hasMore]
  );

  const handleSearchChange = (e) => {
    const value = e.target.value;
    setSearchQuery(value);
    setPage(1);
    setHasMore(true);
    setDoctors([]); // Reset doctor list for new query
  };

  const handleCardClick = (id) => navigate(`/doctor/${id}`);

  const handleBookAppointment = (id) => {
    const isLoggedIn = localStorage.getItem("isLoggedIn") === "true";
    navigate(isLoggedIn ? `/book-appointment/${id}` : "/login");
  };
  return (
    <div className="home-container">
      {/* Hero Section */}
      <div className="hero-section">
        <div className="hero-content">
          <h1>Find Your Perfect Doctor</h1>
          <p>Connect with experienced healthcare professionals for online and clinic consultations</p>
        </div>
      </div>

      {/* Search Bar */}
      <div className="search-section">
        <div className="search-bar">
          <span className="search-icon">üîç</span>
          <input
            type="text"
            placeholder="Search by doctor name or specialty..."
            value={searchQuery}
            onChange={handleSearchChange}
          />
        </div>
      </div>

      {/* Doctors Grid */}
      <div className="doctors-grid">
        {doctors.length === 0 && searchQuery && (
          <div className="no-results">
            <p>No doctors found matching "{searchQuery}"</p>
          </div>
        )}

      {doctors.map((doc, idx) => (
        <div
          className="doctor-card"
          key={doc.id}
          onClick={() => handleCardClick(doc.id)}
          ref={idx === doctors.length - 1 ? lastDoctorRef : null}
        >
          <div className="doctor-header">
            <img src={doc.image_url} alt={doc.name} className="doctor-img" />
            <div className="doctor-info">
              <h2 className="doctor-name">{doc.name}</h2>
              <p className="doctor-role">{doc.bio}</p>
              <p className="doctor-specialties">{doc.specialties}</p>
              <div className="doctor-meta">
                <div>Experience: <strong>{doc.exp}</strong></div>
                <div>Satisfied Patients: <strong>{doc.total_patients}</strong></div>
              </div>
              <div className="consultation-section">
                <div className="consult-box">
                  <div className="consult-title">üñ•Ô∏è Online Video Consultation</div>
                  <div className="available">
                    <span className="status-dot"></span>Available today
                  </div>
                  <div>$.{doc.online_fee}</div>
                </div>
                <div className="consult-box">
                  <div className="consult-title">Clinic Consultation Fee</div>
                  <div className="available">
                    <span className="status-dot"></span>Available today
                  </div>
                  <div>$.{doc.visit_fee}</div>
                </div>
              </div>
            </div>
            <div className="actions">
              <button
                className="btn-outline"
                onClick={(e) => {
                  e.stopPropagation();
                  handleBookAppointment(doc.id);
                }}
              >
                üñ•Ô∏è Video Consultation
              </button>
              <button
                className="btn-primary"
                onClick={(e) => {
                  e.stopPropagation();
                  handleBookAppointment(doc.id);
                }}
              >
                Book Appointment
              </button>
            </div>
          </div>
        </div>
      ))}

      {!hasMore && !searchQuery && (
        <div className="load-complete">
          <p>‚úì All doctors loaded</p>
        </div>
      )}
      </div>
    </div>
  );
};

export default Home;
