import React, { useState, useEffect } from 'react';
import './ShowAppointments.css';

function ShowAppointments() {
  const [appointments, setAppointments] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // Fetch appointments for the logged-in user
    const fetchAppointments = async () => {
      try {
        const userEmail = localStorage.getItem('userEmail');
        const response = await fetch(`http://localhost:3000/appointments?email=${userEmail}`);
        const data = await response.json();
        setAppointments(data);
      } catch (error) {
        console.error('Error fetching appointments:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchAppointments();
  }, []);

  if (loading) {
    return <div className="show-appointments"><p>Loading appointments...</p></div>;
  }

  return (
    <div className="show-appointments">
      <h1>My Appointments</h1>
      {appointments.length === 0 ? (
        <p>No appointments found.</p>
      ) : (
        <table className="appointments-table">
          <thead>
            <tr>
              <th>Doctor Name</th>
              <th>Date</th>
              <th>Time</th>
              <th>Status</th>
            </tr>
          </thead>
          <tbody>
            {appointments.map((appointment) => (
              <tr key={appointment.id}>
                <td>{appointment.doctor_name}</td>
                <td>{appointment.appointment_date}</td>
                <td>{appointment.start_time}</td>
                <td>{appointment.status}</td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
}

export default ShowAppointments;
